mapper.select:
select c.id,c.fullname,dv1.value as appellation,u1.name as owner,c.company,c.job,c.email,c.phone,c.website,c.mphone,dv2.value as state,dv3.value as source,u2.name as create_by,c.create_time,u3.name as edit_by,c.edit_time,c.description,c.contact_summary,c.next_contact_time,c.address from tbl_clue c join tbl_dic_value dv1 on c.appellation=dv1.id left join tbl_dic_value dv2 on c.state=dv2.id left join tbl_dic_value dv3 on c.source=dv3.id join tbl_user u1 on c.owner=u1.id join tbl_user u2 on c.create_by=u2.id left join tbl_user u3 on c.edit_by=u3.id
and c.fullname like concat('%',#{fullname},'%')
and u1.name like concat('%',#{owner},'%')
and c.company like concat('%',#{company},'%')
and c.phone like concat('%',#{phone},'%')
and c.mphone like concat('%',#{mphone},'%')
and c.source=#{source}
and c.state=#{state}
order by c.create_time desc limit #{beginNo},#{pageSize}

mapper.delete:
delete from tbl_clue where id in

  #{id}
;

delete from tbl_clue_remark where clue_id in

  #{id}
;

delete from tbl_clue_activity_relation where clue_id in

  #{id}
;
